//ES6 for React JS

//Implementation with Var 
function hello(){
  for(var i  = 0; i < 5; i++){
    console.log(i);
  }
  console.log(i);
}

hello();

console.log(' ');
//Implementation with Let
function sayHello(){
  for(let i = 0; i < 5; i++){
    console.log(i);
  }
  //i will be limited to for loop scope 
  //will give error 
  //console.log(i);
}

sayHello();

console.log(' ');
//Const Variable also has local scope
const x = 2;
console.log(x);

//x = 3;
//console.log(x);
//will give Error if we reassign Const variable

console.log(' ');
//Implementation of Objects 
const obj = {
  name: 'shivam',
  //Traditional Method for Methods in JS
  walk: function(){},
  //ES6 way for Methods
  talk(){}
}
