console.log('Implementation with Let ->');
function sayHello() {
  for (let i = 0; i < 5; i++) {
    console.log(i);
  }
  //i will be limited to for loop scope 
  console.log('i will be limited to for loop scope ');
  //will give error 
  //console.log(i);
}

sayHello();

